import * as React from 'react'
import "bootstrap/dist/css/bootstrap.css"

import {BrowserRouter as Router , Routes , Route , Link} from 'react-router-dom'

import ShopList from "./components/list-component";


const Login = async(e)=>{
  e.preventDefault();
  const FormDate = new FormDate();

  FormDate.append('email' , email)
  FormDate.append('password' , password)

  await axios.post('http://127.0.0.1:8000/api/login' , FormDate).then(({data})=>{

      navigate('http://127.0.0.1:8000/api/shops')
  }).catch(({response})=>{
      if(response.status == 442) {
          console.log(response.data.errors) 
      }
      else{
          console.log(response.data.message)
      }
  }
  )

}

function App() {
  return (
    <Router>

      <div className="col-lg-12">
         <div className="container">
            <div className="card">
                <div className="card-header">
                  <h3>Login Form</h3>
                </div>
                <div className="card-body">
                       <form onSubmit={Login}>
                        <div className="mb-3">
                            <label for="formGroupExampleInput" class="form-label">Email</label>
                            <input type="text" className="form-control"  placeholder="fawzy@gmail.com" value={email} onChange={(e=>{setEmail(e.target.value)})}/>
                        </div>

                        <div className="mb-3">
                            <label for="formGroupExampleInput2" className="form-label">Password</label>
                            <input type="password" className="form-control" value={password} onChange={(e=>{setPassword(e.target.value)})}/>
                        </div>

                        <div className="mb-3">
                          <button className="btn btn-brimary" value="submit">Save</button>
                        </div>
                     </form>
                </div>

            </div>
         </div>
      </div>


    </Router>
  );
}

export default App;



















