import React ,{useState , useEffect } from "react";
import {BrowserRouter as Router , Routes , Route , Link , useNavigate} from 'react-router-dom'

import "bootstrap/dist/css/bootstrap.css"


import axios from "axios";


import ShopList from './ShopList';

export default function Login(){
  const navigate = useNavigate();
  const [email , setEmail] = useState('');
  const [password , setPassword] = useState('');


  const Login = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('email', email);
    formData.append('password', password);

    await axios.post('http://127.0.0.1:8000/api/login', formData)
      .then(({ data }) => {
        // Redirect to '/api/shops'
        navigate('/api/shops');
      })
      .catch(({ response }) => {
        if (response.status === 442) {
          console.log(response.data.errors);
        } else {
          console.log(response.data.message);
        }
      });
  };

  return (
      <div className="container">
         <div className="row">
            <div className="col-lg-12">
              <div className="card">
                <div className="card-header">
                  <h3>Login</h3>
                </div>
                <div className="card-body">
                <form onSubmit={Login}>


                      <div className="mb-3">
                          <label htmlFor="formGroupExampleInput2" className="form-label">Email</label>
                          <input type="email" name="email" className="form-control" value={email} onChange={(e) => {setEmail(e.target.value)}} placeholder="example@gmail.com" />
                      </div>
                      <div className="mb-3">
                          <label htmlFor="formGroupExampleInput2" className="form-label">Password</label>
                          <input type="password" name="password" className="form-control" value={password} onChange={(e) => {setPassword(e.target.value)}} />
                      </div>
                      <div className="mb-3">
                          <button className="btn btn-primary" type="submit">Save</button>
                      </div>
                  </form>
                </div>
              </div>
            </div>
         </div>
      </div>
  )
}

export function App() {
  return (
    <Router>
      <Switch>
        <Route path="/api/shops" component={ShopList} />
      </Switch>
    </Router>
  );
}



















