import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import "bootstrap/dist/css/bootstrap.css";
import axios from "axios";
import ShopList from './components/list-component';


import app from "./components/app-component";

export default function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('email', email);
    formData.append('password', password);

    try {
      const response = await axios.post('http://127.0.0.1:8000/api/login', formData);
      // Redirect to '/api/shops' after successful login
      navigate('/api/shops');
    } catch (error) {
      if (error.response && error.response.status === 442) {
        console.log(error.response.data.errors);
      } else {
        console.log(error.response.data.message);
      }
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-lg-12">
          <div className="card">
            <div className="card-header">
              <h3>Login</h3>
            </div>
            <div className="card-body">
              <form onSubmit={handleLogin}>
                <div className="mb-3">
                  <label htmlFor="formGroupExampleInput2" className="form-label">Email</label>
                  <input type="email" name="email" className="form-control" value={email} onChange={(e) => { setEmail(e.target.value) }} placeholder="example@gmail.com" />
                </div>
                <div className="mb-3">
                  <label htmlFor="formGroupExampleInput2" className="form-label">Password</label>
                  <input type="password" name="password" className="form-control" value={password} onChange={(e) => { setPassword(e.target.value) }} />
                </div>
                <div className="mb-3">
                  <button className="btn btn-primary" type="submit">Save</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

