import React ,{useState , useEffect} from "react";
import axios from "axios";
import {useNavigate , useParams} from "react-router-dom";




export default function EditShop(){
    

    const navigate = useNavigate();
    const {id} =  useParams();


    const [name , setName] = useState('')
    const [email , setEmail] = useState('')
    const [descirption , setDescirption] = useState('')

    
    useEffect (()=>{
        fetchShops();
    } ,[])


    const fetchShops = async()=>{
        await axios.get('http://127.0.0.1:8000/api/shops/'+id ).then(({data})=>{
            const{name,descirption,email} = data.shop
            setName(name)
            setDescirption(descirption)
            setEmail(email)
            navigate('/')
        }).catch(({response: {data} })=>{
                console.log(data.message) 
          
        }
        )
    }

    const EditShop = async(e)=>{
        e.preventDefault();
        const formData = new formData();
        formData.append('_method' , 'PATCH')
        formData.append('name' , name)
        formData.append('email' , email)
        formData.append('descirption' , descirption)

        await axios.post('http://127.0.0.1:8000/api/shops/' +id , formData).then(({data})=>{
            console.log(data.message)
            navigate('/')
        }).catch(({response})=>{
            if(response.status == 442) {
                console.log(response.data.errors) 
            }
            else{
                console.log(response.data.message)
            }
        }
        )

    }

    return (
        <div className="container">
           <div className="row">
              <div className="col-lg-12">
                <div className="card">
                  <div className="card-header">
                    <h3>Edit Shop</h3>
                  </div>
                  <div className="card-body">
                     <form onSubmit={EditShop}>
                        <div className="mb-3">
                            <label for="formGroupExampleInput" className="form-label">Name</label>
                            <input type="text" className="form-control"  placeholder="Ex:Muhammed Fawzy" value={name} onChange={(e=>{setName(e.target.value)})}/>
                        </div>
                        <div className="mb-3">
                            <label for="formGroupExampleInput2" className="form-label">Email</label>
                            <input type="email" className="form-control" value={email} onChange={(e=>{setEmail(e.target.value)})} placeholder="example@gmail.com"/>
                        </div>
                        <div className="mb-3">
                            <label for="formGroupExampleInput2" className="form-label">Description</label>
                            <textarea  className="form-control"> onChange={(e=>{setDescirption(e.target.value)})}</textarea>
                        </div>
                        <div className="mb-3">
                          <button className="btn btn-brimary" value="submit">Save</button>
                        </div>
                     </form>
                  </div>
                </div>
              </div>
           </div>
        </div>
    )
}
