import React ,{useState , useEffect} from "react";
import axios from "axios";
import {Link} from "react-router-dom";




export default function ListShop(){
    
    const [shops , setShops] = useState([])

    
    useEffect(()=>{
        fetchShops();
    } ,[])


    const fetchShops = async()=>{
        await axios.get('http://127.0.0.1:8000/api/shops' , formData).then(({data})=>{setShops(data)})
    }

    const deleteShop = async(id)=>{
        await axios.delete('http://127.0.0.1:8000/api/shops'+id).then(({data})=>{
            console.log(data.message)
            fetchShops();
        }).catch(({response: {data} })=>{
                console.log(data.message) 
          
        }
        )
    }


    return (
        <div className="container">
           <div className="row">
              <div className="col-lg-12">
                <link className="btn btn-primary" to={"/product/create"}>Create Shop</link>
                <div className="card">
                  <div className="card-header">
                    <h3>Shops</h3>
                  </div>
                  <div className="card-body">
                  <table class="table">
                        <thead>
                            <tr>
                            <th scope="col">Name</th>
                            <th scope="col">Email</th>
                            <th scope="col">Desciption</th>
                            <th scope="col">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                shops.length > 0 && (
                                    shops.map((shop,key) =>(
                                        <tr key={key}>
                                        <td>{shop.name}</td>
                                        <td>{shop.email}</td>
                                        <td>{shop.descirption}</td>
                                        <td>
                                            <button className="btn btn-danger" onClick={()=> deleteShop(shop.id)}>Delete</button>
                                            <link className="btn btn-primary" to={`/product/edit/${shop.id}`}>Edit Shop</link>
                                        </td>
                                        </tr> 
                                    ))
                                )
                            }
                           
                        </tbody>
                        </table>
                  </div>
                </div>
              </div>
           </div>
        </div>
    )
}
